package utils;

import java.nio.charset.StandardCharsets;

import org.apache.commons.codec.binary.Base64;

public class Base64Test {

    /**
     * Data URLs: image encoded in Base64 with a MIME type (image format) and some metadata.
     * data:image/jpg;base64,: This is the metadata part, and it tells us two things:
     *   1.data: indicates that this is a Data URL.
     *   2.image/jpg: Specifies the MIME type, which in this case is a JPEG image.
     *   3.base64,: Indicates that the actual data following this part is encoded in Base64.
     *   4.the remaining part: This is the actual Base64-encoded content of the image.
     * @param args
     */
    public static void main(String[] args) {
        String dataURL = "data:image/jpg;base64,";
        String[] dataUrls=dataURL.split(",");
        checkJavaUtilsBase64(dataUrls[1]);
//        checkCodecBase64(dataUrls[1]);
    }

    private static void checkJavaUtilsBase64(String str) {
        byte[] decode1 = java.util.Base64.getDecoder().decode(str);
        String str1 = encodeBase64(decode1);
        System.out.println(str1);
        System.out.println(str.equals(str1));
    }

    public static String decodeBase64(final String str) {
        return new String(java.util.Base64.getDecoder().decode(str), StandardCharsets.UTF_8);
    }

    public static String encodeBase64(final byte[] decode1) {
        return new String(java.util.Base64.getEncoder().encode(decode1), StandardCharsets.UTF_8);
    }


    private static void checkCodecBase64(String str) {
        byte[] bytes = Base64.decodeBase64(str);
        System.out.println(new String(bytes, StandardCharsets.UTF_8));
        String str1 = new String(Base64.encodeBase64(bytes));
        System.out.println(str1);
        System.out.println(str.equals(str1));
    }
}
